# Helper function to get pages by category
def get_pages_by_category(category):
    return [page for page in seo_pages if page['category'] == category]

# Helper function to get all categories
def get_all_categories():
    return list(set(page['category'] for page in seo_pages))

# Helper function to get related pages (same category)
def get_related_pages(current_page, limit=5):
    return [page for page in seo_pages 
            if page['category'] == current_page['category'] and page['slug'] != current_page['slug']][:limit] 